mapper.update:
update event
set event_title = #{eventTitle}, event_content = #{eventContent}, event_type = #{eventType}, mbId = #{mbId},
	event_start_date = #{eventStartDate}, event_end_date = #{eventEndDate}, event_repeat = #{eventRepeat},
	all_day = #{allDay}, couple_no = #{coupleNo}
where event_no = #{eventNo}
